package inheritance;

public class Childoveride extends Override {

	public void sound() {
		System.out.println("I am Robot");
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Childoveride obj = new Childoveride();
		obj.sound();
		
		
		

	}

}
// in this polymorphism is done with overriding ( run time polymorphism)